package loops;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	for(int i=11;i<10;i++) {
		System.out.println(i);
	}
	int j=0;
	while(j<10) {
		//System.out.println("hello"+""+j);
		j++;
	}
	int h=9;
	do {
		System.out.println(h);
	}while(h>10);

	}

}
